class Solution:
    def validPalindrome(self, s: str) -> bool:
        for i in range(len(s) // 2):
            if s[i] != s[~i]:
                l = s[i:~i]
                r = s[i + 1:len(s) - i]
                return l == l[::-1] or r == r[::-1]
        return True


def main():
    s = Solution()
    print(s.validPalindrome(
        ""))


if __name__ == "__main__":
    main()

