class Solution:
    def validPalindrome(self, s: str) -> bool:
        for i in range(len(s)):
            if s[:i] + s[i + 1:] == [s[:i] + s[i + 1:]][0][::-1]:
                return True
        return False


def main():
    s = Solution()
    print(s.validPalindrome(
        ""))


if __name__ == "__main__":
    main()

